IMAGE_ID, POST_ID, IMAGE_URL, CREATED_DATE, CREATED_ID, UPDATED_DATE, UPDATED_ID
) VALUES (
#{imageId}, #{postId}, #{imageUrl}, SYSTIMESTAMP, #{createdId}, SYSTIMESTAMP, #{updatedId}
)

INSERT ALL

    INTO TB_POST_IMAGE (
        IMAGE_ID, POST_ID, IMAGE_URL, CREATED_DATE, CREATED_ID, UPDATED_DATE, UPDATED_ID
    ) VALUES (
        SEQ_TB_POST_IMAGE.NEXTVAL, #{image.postId}, #{image.imageUrl}, SYSTIMESTAMP, #{image.createdId}, SYSTIMESTAMP, #{image.updatedId} 
    )

SELECT * FROM DUAL
